import React from 'react'
import "./home.css"
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
//import Card from "react-bootstrap/Card"
//import Row from "react-bootstrap/Row"
import Dropdown from 'react-bootstrap/Dropdown';
//use Navigate hook 
//we can also use nevlink
import {useNavigate}from "react-router-dom";
import Tables from '../../components/Tables/Tables';
import Spiner from "../../components/Spiner/Spiner"
import Spinner from 'react-bootstrap/Spinner';





const Home = () => {

    const navigate =useNavigate();
    const adduser=()=>{
        navigate("/register")
    }
    return (
        <>
            <div className="container">
                <div className="main_div">
                    {/* Search add button */}
                    <div className="search_add mt-4 d-flex justify-content-between">
                        <div className="search col-lg-4">
                            <Form className="d-flex">
                                <Form.Control
                                    type="search"
                                    placeholder="Search"
                                    className="me-2"
                                    aria-label="Search"
                                />
                                <Button variant="success" className='search_btn'>Search</Button>
                            </Form>
                        </div>
                        <div className="add_btn">

                            <Button variant="primary" onClick={adduser} className='search_btn'>  <i className="fa-solid fa-plus"></i>&nbsp;Add User</Button>
                        </div>
                    </div>
                    {/* export .gender,status */}
                    <div className="filter_div mt-5 d-flex justify-content-between flex-wrap">
                        <div className="export_csv">
                            <Button variant="primary" className='search_btn'>Export To CSV</Button>
                        </div>
                        <div className="filter_gender">
                            <div className="filter"></div>
                            <h3>Filter By Gender</h3>
                            <div className="gernder d-flex justify-content-around">
                                <Form.Check
                                    type={"radio"}
                                    label={`All`}
                                    name="gender"
                                    value={"All"}
                                    defaultChecked
                                />

                                <Form.Check
                                    type={"radio"}
                                    label={`Male`}
                                    name="gender"
                                    value={"Male"}

                                />
                                <Form.Check
                                    type={"radio"}
                                    label={`Female`}
                                    name="gender"
                                    value={"Female"}

                                />
                            </div>
                        </div>
                         {/* Short by value */}
                    <div className="filter_newols">
                        <h3>Short by Value</h3>
                        <Dropdown className="text-center">
                            <Dropdown.Toggle className='dropdown_btn' id="dropdown-basic">
                            <i className="fa-solid fa-sort"></i>
                            </Dropdown.Toggle>

                            <Dropdown.Menu>
                                <Dropdown.Item>New </Dropdown.Item>
                                <Dropdown.Item>Old</Dropdown.Item>
                                
                            </Dropdown.Menu>
                        </Dropdown>

                    </div>
                    {/* filter by status */}
                    <div className="filter_status">
                        <div className="status">
                            <h3>Filter By status</h3>
                            <div className="status_radio d-flex justify-content-around flex-wrap">
                            <Form.Check
                                    type={"radio"}
                                    label={`All`}
                                    name="status"
                                    value={"All"}
                                    defaultChecked
                                />

                                <Form.Check
                                    type={"radio"}
                                    label={`Active`}
                                    name="status"
                                    value={"Active"}

                                />
                                <Form.Check
                                    type={"radio"}
                                    label={`InActive`}
                                    name="status"
                                    value={"InActive"}

                                />
                            </div>
                        </div>
                    </div>
                    </div>
                   
                </div>
                {
                    Spinner ?<Spiner/>:<Tables/>
                }
                
            </div>
        </>
    )
}

export default Home
